        // Define our sample name
        final java.lang.String SAMPLE_NAME = "$SAMPLE_NAME$";

        // create our actual sample
        net.beadsproject.beads.data.Sample sample = net.beadsproject.beads.data.SampleManager.sample(SAMPLE_NAME);

        // test if we opened the sample successfully
        if (sample != null) {
            // Create our sample player
            net.beadsproject.beads.ugens.SamplePlayer samplePlayer = new net.beadsproject.beads.ugens.SamplePlayer(sample);
            // Samples are killed by default at end. We will stop this default actions so our sample will stay alive
            samplePlayer.setKillOnEnd(false);

            // Connect our sample player to audio
            net.beadsproject.beads.ugens.Gain gainAmplifier = new net.beadsproject.beads.ugens.Gain(NUMBER_AUDIO_CHANNELS, audioVolume);
            gainAmplifier.addInput(samplePlayer);
            hb.ac.out.addInput(gainAmplifier);

            /******** Write your code below this line ********/
            $END$
            /******** Write your code above this line ********/
        }
        else
        {
            hb.setStatus("Failed sample " + SAMPLE_NAME);
        }
