final float INITIAL_FREQUENCY = $FREQUENCY$; // this is the frequency of the waveform we will make
final float INITIAL_VOLUME = $VOLUME$; // define how loud we want the sound

net.beadsproject.beads.ugens.Glide audioFrequency = new net.beadsproject.beads.ugens.Glide(INITIAL_FREQUENCY);
net.beadsproject.beads.ugens.Glide audioVolume = new net.beadsproject.beads.ugens.Glide(INITIAL_VOLUME);


// create a wave player to generate a waveform based on frequency and waveform type
net.beadsproject.beads.ugens.WavePlayer waveformGenerator = new net.beadsproject.beads.ugens.WavePlayer(audioFrequency, net.beadsproject.beads.data.Buffer.SINE);

// set up a gain amplifier to control the volume
net.beadsproject.beads.ugens.Gain gainAmplifier = new net.beadsproject.beads.ugens.Gain(NUMBER_AUDIO_CHANNELS, audioVolume);

// connect our WavePlayer object into the Gain object
gainAmplifier.addInput(waveformGenerator);

// Now plug the gain object into the audio output
hb.ac.out.addInput(gainAmplifier);
$END$

